bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {

    case KC_DEL:
        if (get_mods() & MOD_MASK_SHIFT) {
            if (record->event.pressed) {
                register_code(KC_INS);
            } else {
                unregister_code(KC_INS);
            }
            return false;
        }
        return true;
    }
    return true;
};


